import React from 'react';
import { 
  Container, 
  Typography, 
  Button, 
  Grid, 
  Card, 
  CardContent, 
  CardMedia, 
  styled
} from '@mui/material';
import { Link } from 'react-router-dom';

const HeroSection = styled('div')(({ theme }) => ({
  height: '60vh',
  backgroundImage: 'url(https://source.unsplash.com/1600x900/?travel)',
  backgroundSize: 'cover',
  backgroundPosition: 'center',
  display: 'flex',
  alignItems: 'center',
  justifyContent: 'center',
  color: 'white',
  textAlign: 'center',
  position: 'relative',
  '&::before': {
    content: '""',
    position: 'absolute',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    backgroundColor: 'rgba(0,0,0,0.5)',
  },
}));

const HeroContent = styled('div')(({ theme }) => ({
  position: 'relative',
  zIndex: 1,
}));

const StyledCard = styled(Card)(({ theme }) => ({
  height: '100%',
  display: 'flex',
  flexDirection: 'column',
}));

const StyledSection = styled('div')(({ theme }) => ({
  padding: theme.spacing(8, 0),
}));

function Home() {
  const features = [
    {
      title: 'Discover Places',
      description: 'Find amazing destinations and attractions worldwide.',
      image: 'https://source.unsplash.com/400x300/?destination',
    },
    {
      title: 'Plan Itineraries',
      description: 'Create detailed travel plans with our easy-to-use tools.',
      image: 'https://source.unsplash.com/400x300/?planning',
    },
    {
      title: 'Book Hotels',
      description: 'Find and book the perfect accommodation for your stay.',
      image: 'https://source.unsplash.com/400x300/?hotel',
    },
  ];

  return (
    <div>
      <HeroSection>
        <HeroContent>
          <Container maxWidth="md">
            <Typography variant="h2" gutterBottom>
              Plan Your Perfect Trip
            </Typography>
            <Typography variant="h5" paragraph>
              Discover amazing destinations and create unforgettable memories
            </Typography>
            <Button
              component={Link}
              to="/search"
              variant="contained"
              color="primary"
              size="large"
            >
              Start Planning
            </Button>
          </Container>
        </HeroContent>
      </HeroSection>

      <StyledSection>
        <Container maxWidth="lg">
          <Typography variant="h4" align="center" gutterBottom>
            Why Choose Us?
          </Typography>
          <Grid container spacing={4} style={{ marginTop: '2rem' }}>
            {features.map((feature) => (
              <Grid item key={feature.title} xs={12} sm={6} md={4}>
                <StyledCard>
                  <CardMedia
                    component="img"
                    height="200"
                    image={feature.image}
                    alt={feature.title}
                  />
                  <CardContent>
                    <Typography gutterBottom variant="h5" component="h2">
                      {feature.title}
                    </Typography>
                    <Typography>
                      {feature.description}
                    </Typography>
                  </CardContent>
                </StyledCard>
              </Grid>
            ))}
          </Grid>
        </Container>
      </StyledSection>
    </div>
  );
}

export default Home;
